import {Promise} from 'es6-promise';
import qs from '../src/qs';



let p = new Promise((resolve, reject) => {
    resolve('promise value');
});

let perr = new Promise((resolve, reject) => {
  setTimeout( () => {
    reject('promise error happen');
  }, 2000);
});

qs.fromPromise(p)
  .onValue( v => {
    console.log(v);
  })
  .onEnd( () => console.log('promise end'));


qs.fromPromise(perr)
  .onError( err => console.log('promise err', err))
  .onEnd( () => console.log('promise error end'));
