function emitter(obs) {

    function value(x) {
        obs.emitValue(x);
        return obs.state.active;
    }

    function event(e) {
        obs.emit(e.type, e.value);
        return obs.state.active;
    }

    function end() {
        obs.emitEnd();
        return obs.state.active;
    }

    function error(e) {
        obs.emitError(e);
        return obs.state.active;
    }

    return {
        value,
        event,
        error,
        emit: value,
        emitEvent: event,
        end: end
    };
}


export default emitter;