import Property from '../Property';
import {VALUE} from '../constants/events';
import extend from '../polyfill/extend';


function Constant(value) {
    this._currentEvent = {type: VALUE, value};
}

Constant.prototype = Object.create(Property.prototype);

extend(Constant.prototype, {
    state: {
        alive: false,
        active: false
    },
    _activating: false
});

module.exports = function constant(value) {
    return new Constant(value);
};